import React, { Component } from 'react'
import Keyboard from './Keyboard.js'
import Riddle from './Riddle.js'
import './App.css'
import words from './resources/words_EN.js'
import HangmanCanvas from './HangmanCanvas.js'

const LETTERS = "ABCDEFGHIJKLMNOPQRSTUVWXYZ".split('')

class App extends Component {
  state = {
    attemptedKeys: [],
    wordToGuess: this.randomWord(),
    numberOfErrors: 1
  }

  // Putting an arrow function to bind this here...
  clickKey = (key) => {
    const {attemptedKeys, numberOfErrors} = this.state
    if (!attemptedKeys.includes(key)) {
      const newNumberOfErrors = numberOfErrors + 1
      const newAttemptedKeys = [...attemptedKeys, key]
      this.setState( {
        attemptedKeys: newAttemptedKeys,
        numberOfErrors: newNumberOfErrors
      } )
    }
  }

  // Putting an arrow function to bind this here...
  isRevealed = (key) => {
    const {attemptedKeys} = this.state
    return attemptedKeys.includes(key)
  }

  // Putting an arrow function to bind this here...
  getStatus = (key) => {
    const { attemptedKeys } = this.state
    if (attemptedKeys.includes(key)) {
      return "clicked"
    }
    return "unclicked"
  }

  randomWord() {
    const randomIndex = Math.floor(Math.random() * words.length)
    return words[randomIndex].split('')
  }

  resetGame = () => {
    this.setState({ 
        attemptedKeys: [], 
        wordToGuess: this.randomWord(),
        numberOfErrors: 0
      })
  }

  render() {
    const { wordToGuess, attemptedKeys, numberOfErrors } = this.state
    const won = wordToGuess.every( key => attemptedKeys.includes(key))
    return (
      <div className="pendu">
        <HangmanCanvas numberOfErrors={numberOfErrors} />
        <Riddle word={wordToGuess} isRevealed={this.isRevealed} />
        <Keyboard keys={LETTERS} onClick={this.clickKey} status={this.getStatus} won={won} reset={this.resetGame} />
      </div>
    )
  }

}
export default App;
