import React from 'react'
import PropTypes from 'prop-types'
import './Keyboard.css'

const STATUS = new Map([['won', 'ðŸ¤—'],['lost', 'ðŸ˜¤']])

const Keyboard = ({ keys, onClick, status, gameStatus, reset, wordToGuess }) => (
    <div className="keyboard">
        { 
            keys.map( (key) => (
                <span className={`key ${status(key)}`} key={key} onClick={() => onClick(key)}>{key}</span>
            ))
        }
    </div>
)

Keyboard.propTypes = {
    keys: PropTypes.arrayOf(PropTypes.string.isRequired).isRequired,
    onClick: PropTypes.func.isRequired,
    status: PropTypes.func.isRequired,
    gameStatus: PropTypes.string.isRequired,
    reset: PropTypes.func.isRequired,
    wordToGuess: PropTypes.string.isRequired,
}

Keyboard.defaultProps = {
    gameStatus: 'playing'
}

export default Keyboard
