import React, {Component} from 'react'


class Canvas extends Component {

    constructor() {
        super()
        this.canvas = React.createRef()
    }

    componentDidMount() {
        const canvas = this.canvas.current
        const ctx = canvas.getContext("2d")

        ctx.strokeRect(0, 0, 100, 100)

        ctx.beginPath()
        ctx.moveTo(10, 90)
        ctx.lineTo(40, 90)
        ctx.moveTo(25, 90)
        ctx.lineTo(25, 20)
        ctx.lineTo(70, 20)
        ctx.closePath()
        ctx.stroke()
    }

    render() {
        return (
            <div>
                <canvas ref={this.canvas} height={100} width={100}/>
            </div>
        )
    }
}

export default Canvas
