import PropTypes from 'prop-types'
import React, { Component } from 'react'

const RED_COLOR = 'rgb(200, 0, 0)'
const HANGMAN_STEPS = [
    (ctx) => {
        ctx.moveTo(10, 90)
        ctx.lineTo(40, 90)
    },
    (ctx) => {
        ctx.moveTo(25, 90)
        ctx.lineTo(25, 20)
    },
    (ctx) => {
        ctx.moveTo(24, 20)
        ctx.lineTo(70, 20)
    },
    (ctx) => {
        ctx.moveTo(25, 35)
        ctx.lineTo(40, 20)
    },
    (ctx) => {
        ctx.moveTo(70, 19)
        ctx.lineTo(70, 35)
    },
    (ctx) => {
        // Head
        ctx.moveTo(75, 35)
        ctx.arc(70, 40, 5, 0, Math.PI * 2, true)
    },
    (ctx) => {
        // Body
        ctx.moveTo(70, 45)
        ctx.lineTo(70, 63)
    },
    (ctx) => {
        // Left leg
        ctx.moveTo(70, 63)
        ctx.lineTo(63, 78)
    },
    (ctx) => {
        // Right leg
        ctx.moveTo(70, 63)
        ctx.lineTo(77, 78)
    },
    (ctx) => {
        // Left arm
        ctx.moveTo(70, 55)
        ctx.lineTo(60, 50)
    },
    (ctx) => {
        // Right arm
        ctx.moveTo(70, 55)
        ctx.lineTo(80, 50)
    }
]

class HangmanCanvas extends Component {

    constructor(props) {
        super(props)
        this.canvas = React.createRef()
    }

    componentDidMount() {
        this.resetCanvas()
    }

    shouldComponentUpdate({ numberOfErrors }) {
        if (numberOfErrors === 0) {
            this.resetCanvas()
        }
        return this.props.numberOfErrors !== numberOfErrors
    }

    resetCanvas() {
        const canvas = this.canvas.current
        const ctx = canvas.getContext("2d")

        ctx.clearRect(0, 0, 150, 253)
        ctx.strokeRect(0, 0, 150, 253)
        this.drawHangmanSteps(0, this.props.maxErrorsAuthorized - 1, ctx)
    }

    drawHangmanSteps(startStepIndex, endStepIndex, ctx) {
        console.log(`drawHangmanSteps ${startStepIndex} ${endStepIndex}`)
        ctx.beginPath()
        HANGMAN_STEPS.slice(startStepIndex, endStepIndex).forEach(step => step(ctx))
        ctx.closePath()
        ctx.stroke()
    }

    componentDidUpdate({ maxErrorsAuthorized }) {
        const canvas = this.canvas.current
        const ctx = canvas.getContext("2d")
        const newNumberOfErrors = this.props.numberOfErrors
        const startStepIndex = HANGMAN_STEPS.length - maxErrorsAuthorized
        const endStepIndex = startStepIndex + newNumberOfErrors

        if (endStepIndex === HANGMAN_STEPS.length) {
            ctx.fillStyle = RED_COLOR
            ctx.fillRect(1, 1, 99, 99)
        }

        this.drawHangmanSteps(0, endStepIndex, ctx)
    }

    render() {
        return (
            <div>
                <canvas ref={this.canvas} height={100} width={100} />
            </div>
        )
    }
}

HangmanCanvas.propTypes = {
    numberOfErrors: PropTypes.number.isRequired,
    maxErrorsAuthorized: PropTypes.number.isRequired,
}

HangmanCanvas.defaultProps = {
    numberOfErrors: 0,
}

export default HangmanCanvas
