import React, { Component } from 'react'
import Keyboard from './Keyboard.js'
import Riddle from './Riddle.js'
import './App.css'

const LETTERS = "ABCDEFGHIJKLMNOPQRSTUVWXYZ".split('')

class App extends Component {
  state = {
    attemptedKeys: [],
    wordToGuess: 'COMPUTER'.split(''),
  }

  // Putting an arrow function to bind this here...
  clickKey = (key) => {
    const {attemptedKeys} = this.state
    if (!attemptedKeys.includes(key)) {
      const newAttemptedKeys = [...attemptedKeys, key]
      this.setState( {attemptedKeys: newAttemptedKeys} )
    }
  }

  isRevealed = (key) => {
    const {wordToGuess} = this.state
    return wordToGuess.includes(key)
  }

  // Putting an arrow function to bind this here...
  getStatus = (key) => {
    const { attemptedKeys } = this.state
    if (attemptedKeys.includes(key)) {
      return "clicked"
    }
    return "unclicked"
  }

  render() {
    const { wordToGuess, attemptedKeys } = this.state
    const won = attemptedKeys.every( key => wordToGuess.includes(key))
    return (
      <div className="pendu">
        <Riddle word={wordToGuess} isRevealed={this.isRevealed} />
        <Keyboard keys={LETTERS} onClick={this.clickKey} status={this.getStatus} />
        { won && <span>GAGNE !</span> }
      </div>
  )
}

}
export default App;
