import React, { Component } from 'react'
import Keyboard from './Keyboard.js'
import Riddle from './Riddle.js'
import './App.css'

const LETTERS = "ABCDEFGHIJKLMNOPQRSTUVWXYZ".split('')
const wordToGuess = 'COMPUTER'

class App extends Component {
  state = {
    attemptedKeys: [],
    foundKeyIndexes: [],
  }

  // Putting an arrow function to bind this here...
  clickKey = (key) => {
    const newAttemptedKeys = [...attemptedKeys, key]
    this.setState( {attemptedKeys: newAttemptedKeys} )
  }

  // Putting an arrow function to bind this here...
  getStatus = (key) => {
    const { attemptedKeys } = this.state
    if (attemptedKeys.includes(key)) {
      return "clicked"
    }
    return "unclicked"
  }

  render() {
    return (
      <div className="pendu">
        <Riddle word={wordToGuess} />
        <Keyboard keys={LETTERS} onClick={this.clickKey} status={this.getStatus} />
      </div>
  )
}

}
export default App;
