import React, { Component } from 'react'
import Keyboard from './Keyboard.js'
import Riddle from './Riddle.js'
import './App.css'
import words from './resources/words_EN.js'

const LETTERS = "ABCDEFGHIJKLMNOPQRSTUVWXYZ".split('')
const DEFAULT_STATE = {
  attemptedKeys: [],
  wordToGuess: words[Math.floor(Math.random() * words.length)].split(''),
}

class App extends Component {
  state = DEFAULT_STATE

  // Putting an arrow function to bind this here...
  clickKey = (key) => {
    const {attemptedKeys} = this.state
    if (!attemptedKeys.includes(key)) {
      const newAttemptedKeys = [...attemptedKeys, key]
      this.setState( {attemptedKeys: newAttemptedKeys} )
    }
  }

  isRevealed = (key) => {
    const {attemptedKeys} = this.state
    return attemptedKeys.includes(key)
  }

  // Putting an arrow function to bind this here...
  getStatus = (key) => {
    const { attemptedKeys } = this.state
    if (attemptedKeys.includes(key)) {
      return "clicked"
    }
    return "unclicked"
  }

  render() {
    const { wordToGuess, attemptedKeys } = this.state
    const won = wordToGuess.every( key => attemptedKeys.includes(key))
    return (
      <div className="pendu">
        <Riddle word={wordToGuess} isRevealed={this.isRevealed} />
        <Keyboard keys={LETTERS} onClick={this.clickKey} status={this.getStatus} won={won} />
        { won && <span>GAGNE !</span> }
      </div>
    )
  }

}
export default App;
