        // ctx.lineTo(60, 50)
        // ctx.moveTo(70, 55)
        // ctx.lineTo(80, 50)
        // ctx.closePath()
        // ctx.stroke()
    }

    static getDerivedStateFromProps({ numberOfErrors }, state) {
        console.log(`New number of errors: ${numberOfErrors}, ${state}`)
    }

    render() {
        return (
            <div>
                <canvas ref={this.canvas} height={100} width={100}/>
            </div>
        )
    }

}

HangmanCanvas.propTypes = {
    numberOfErrors: PropTypes.number.isRequired,
}

HangmanCanvas.defaultProps = {
    numberOfErrors: 0,
}

export default HangmanCanvas
