import PropTypes from 'prop-types'
import React, {Component} from 'react'


class HangmanCanvas extends Component {

    constructor(props) {
        super(props)
        this.canvas = React.createRef()
        this.state = {props}
    }

    componentDidMount() {
        console.log("componentDidMount")
        const canvas = this.canvas.current
        const ctx = canvas.getContext("2d")

        ctx.strokeRect(0, 0, 100, 100)

        ctx.beginPath()
        ctx.moveTo(10, 90)
        ctx.lineTo(40, 90)
        ctx.moveTo(25, 90)
        ctx.lineTo(25, 20)
        ctx.moveTo(25, 20)
        ctx.lineTo(70, 20)
        ctx.moveTo(25, 35)
        ctx.lineTo(40, 20)
        ctx.moveTo(70, 20)
        ctx.lineTo(70, 35)
        ctx.moveTo(75, 40)
        // ctx.lineTo(80, 50)
        ctx.closePath()
        ctx.stroke()
    }

    static getDerivedStateFromProps({ numberOfErrors }) {
        console.log(`New number of errors: ${numberOfErrors}`)


        // // Head
        // ctx.arc(70, 40, 5, 0, Math.PI * 2, false)
        // // Body
        // ctx.moveTo(70, 45)
        // ctx.lineTo(70, 63)
        // // Legs
        // ctx.moveTo(70, 63)
        // ctx.lineTo(63, 78)
        // ctx.moveTo(70, 63)
        // ctx.lineTo(77, 78)
        // // Arms
        // ctx.moveTo(70, 55)
        // ctx.lineTo(60, 50)
        // ctx.moveTo(70, 55)
        // ctx.lineTo(80, 50)
        // ctx.closePath()
        // ctx.stroke()

        return null
    }

    render() {
        return (
            <div>
                <canvas ref={this.canvas} height={100} width={100}/>
            </div>
        )
    }

}

HangmanCanvas.propTypes = {
    numberOfErrors: PropTypes.number.isRequired,
}

HangmanCanvas.defaultProps = {
    numberOfErrors: 0,
}

export default HangmanCanvas
