import React from 'react'
import './Keyboard.css'

const Keyboard = ({ letters }) => (
    <div className="keyboard">
        { letters.map( (letter, index) => (
            <span className="letter clicked" key={index}>{letter}</span>
        ))}
    </div>
)

export default Keyboard
